-- ----------------------------
--  Table structure for `admin_base_menu`
-- ----------------------------
DROP TABLE IF EXISTS `admin_base_menu`;
CREATE TABLE `admin_base_menu` (
  `menu_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '菜单id',
  `menu_parent_id` int(10) NOT NULL DEFAULT '0' COMMENT '菜单id',
  `menu_rel` varchar(20) NOT NULL DEFAULT 'pageTab',
  `menu_name` varchar(20) NOT NULL COMMENT '菜单名称',
  `menu_label` varchar(50) NOT NULL DEFAULT '' COMMENT '菜单text',
  `menu_order` tinyint(4) unsigned NOT NULL DEFAULT '50' COMMENT '排序',
  `menu_enable` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否启用(BOOL)',
  `menu_class` varchar(255) NOT NULL DEFAULT '' COMMENT 'class name',
  `menu_icon` varchar(30) NOT NULL DEFAULT '' COMMENT '图标class',
  `menu_bubble` varchar(255) NOT NULL DEFAULT '' COMMENT 'bubble',
  `menu_url_mdu` varchar(255) NOT NULL DEFAULT '' COMMENT '模块名称',
  `menu_url_ctl` varchar(50) NOT NULL DEFAULT '' COMMENT '控制器名称',
  `menu_url_met` varchar(20) NOT NULL DEFAULT '' COMMENT '控制器方法',
  `menu_url_parem` varchar(250) NOT NULL DEFAULT '' COMMENT 'url参数',
  `menu_url` varchar(100) NOT NULL DEFAULT '' COMMENT '类型 1 页面 2 url',
  `menu_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' ON UPDATE CURRENT_TIMESTAMP COMMENT '最后更新时间',
  `menu_type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '菜单类型(LIST):1-通用;2-订单系统;3-商城系统',
  `menu_note` varchar(5000) NOT NULL DEFAULT '',
  `menu_func` varchar(255) NOT NULL DEFAULT '' COMMENT '功能开启:跟设置config_key',
  `menu_role` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '角色类型(LIST):1-平台;2-商检管理端;3-门店',
  PRIMARY KEY (`menu_id`)
) ENGINE=InnoDB AUTO_INCREMENT=100909 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='菜单表-10个递增';

