DROP TABLE IF EXISTS `cms_article_base_user`;
CREATE TABLE `cms_article_base_user` (
  `article_id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '文章Id',
  `user_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '文章作者Id',
  `article_time` int(10) NOT NULL COMMENT '文章日期',
  `article_status` varchar(20) NOT NULL DEFAULT 'publish' COMMENT '文章状态[ ''draft'' | ''publish'' | ''pending''| ''future'' | ''private'' ] ',
  `article_type` varchar(20) NOT NULL DEFAULT 'post' COMMENT '[ ''post'' | ''page'' | ''link'' | ''nav_menu_item'' | custom post type ]  文章类型：文章、页面、链接、菜单、其他定制类型   post表示为已发布的文章，revision表示为修订版本，page为页面',
  PRIMARY KEY (`article_id`),
  KEY `type_status_date` (`article_type`,`article_status`,`article_time`,`article_id`),
  KEY `article_author` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='文章用户关联表-不使用，效率问题使用redis优化';

