-- ----------------------------
--  Table structure for `shop_order_base`
-- ----------------------------
DROP TABLE IF EXISTS `shop_order_base`;
CREATE TABLE `shop_order_base` (
  `order_id` varchar(50) NOT NULL DEFAULT '' COMMENT '订单Id',
  `order_number` varchar(50) NOT NULL DEFAULT '' COMMENT '订单编号 商城内部使用',
  `order_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '下单时间',
  `order_payment_amount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '应付金额/应支付金额:order_goods_amount - order_discount_amount + order_shipping_fee - order_voucher_price - order_points_fee - order_adjust_fee',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '卖家店铺id',
  `store_name` varchar(50) NOT NULL DEFAULT '' COMMENT '卖家店铺名称',
  `buyer_user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '买家id',
  `buyer_user_name` varchar(50) NOT NULL DEFAULT '' COMMENT '买家姓名',
  `order_state_id` smallint(4) NOT NULL DEFAULT '2010' COMMENT '订单状态(LIST):2011-待订单审核;2013-待财务审核;2020-待配货/待出库审核;2030-待发货;2040-已发货/待收货确认;2060-已完成/已签收;2070-已取消/已作废;',
  PRIMARY KEY (`order_id`),
  KEY `order_state_id` (`order_state_id`),
  KEY `buyer_user_id` (`buyer_user_id`,`order_state_id`),
  KEY `store_id` (`store_id`,`order_state_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='订单详细信息-检索不分表也行，cache';

