--  Table structure for `shop_order_chain_code`
-- ----------------------------
DROP TABLE IF EXISTS `shop_order_chain_code`;
CREATE TABLE `shop_order_chain_code` (
  `order_id` varchar(50) NOT NULL COMMENT '订单id',
  `chain_id` int(11) NOT NULL DEFAULT '0' COMMENT '门店编号',
  `item_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单商品id',
  `chain_code` varchar(50) NOT NULL DEFAULT '' COMMENT '虚拟码:pickupcode',
