-- ----------------------------
--  Table structure for `shop_order_comment`
-- ----------------------------
DROP TABLE IF EXISTS `shop_order_comment`;
CREATE TABLE `shop_order_comment` (
  `order_id` varchar(50) NOT NULL DEFAULT '' COMMENT '订单Id',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '卖家店铺id-冗余',
  `store_name` varchar(50) NOT NULL DEFAULT '' COMMENT '店铺名称',
  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '买家id',
  `user_name` varchar(50) NOT NULL DEFAULT '' COMMENT '买家姓名',
  `comment_points` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '获得积分-冗余，独立表记录',
  `comment_scores` tinyint(4) unsigned NOT NULL DEFAULT '3' COMMENT '评价星级1-5积分',
  `comment_content` varchar(255) NOT NULL DEFAULT '' COMMENT '评价内容',
  `comment_image` varchar(1000) NOT NULL DEFAULT '' COMMENT '评论上传的图片：|分割多张图片',
  `comment_helpful` int(10) NOT NULL DEFAULT '0' COMMENT '有帮助',
  `comment_nohelpful` int(10) NOT NULL DEFAULT '0' COMMENT '无帮助',
  `comment_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '评价时间',
  `comment_is_anonymous` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '匿名评价',
  `comment_enable` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '评价信息的状态(BOOL): 1-正常显示; 0-禁止显示',
