-- ----------------------------
--  Table structure for `shop_order_info`
-- ----------------------------
DROP TABLE IF EXISTS `shop_order_info`;
CREATE TABLE `shop_order_info` (
  `order_id` varchar(50) NOT NULL DEFAULT '' COMMENT '订单Id',
