  `order_month` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '订单月份-索引查询',
  `order_day` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '订单日-索引查询',
  `order_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '下单时间:检索使用',
