  `order_buyer_hidden` tinyint(1) NOT NULL DEFAULT '0' COMMENT '买家删除(BOOL): 1-是; 0-否',
  `order_shop_hidden` tinyint(1) NOT NULL DEFAULT '0' COMMENT '店铺删除(BOOL): 1-是; 0-否',
  `payment_type_id` int(4) unsigned NOT NULL DEFAULT '2' COMMENT '支付方式(ENUM): 1301-货到付款; 1302-在线支付; 1303-白条支付; 1304-现金支付; 1305-线下支付; ',
  `payment_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '付款时间',
  `order_state_id` smallint(4) unsigned NOT NULL DEFAULT '10' COMMENT '订单状态(LIST):2011-待订单审核;2013-待财务审核;2020-待配货/待出库审核;2030-待发货;2040-已发货/待收货确认;2060-已完成/已签收;2070-已取消/已作废;',
  `order_is_review` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '订单审核(BOOL):0-未审核;1-已审核;',
  `order_finance_review` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '财务状态(BOOL):0-未审核;1-已审核',
  `order_is_paid` smallint(4) unsigned NOT NULL DEFAULT '3010' COMMENT '付款状态(ENUM):3010-未付款;3011-付款待审核;3012-部分付款;3013-已付款',
  `order_is_out` smallint(4) unsigned NOT NULL DEFAULT '3020' COMMENT '出库状态(ENUM):3020-未出库;3021-部分出库通过拆单解决这种问题;3022-已出库',
  `order_is_shipped` smallint(6) NOT NULL DEFAULT '3030' COMMENT '发货状态(ENUM):3030-未发货;3031-已发货;3032-部分发货',
  `order_is_received` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '收货状态(BOOL):0-未收货;1-已收货',
  `chain_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '门店id',
  `delivery_type_id` tinyint(4) NOT NULL DEFAULT '0' COMMENT '配送方式',
  `order_is_offline` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '线下订单(BOOL):0-线上;1-线下',
  `cart_type_id` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '购买类型(ENUM):1-购买; 2-积分兑换; 3-赠品; 4-活动促销',
  PRIMARY KEY (`order_id`),
  KEY `order_state_id` (`order_state_id`),
  KEY `kind_id` (`kind_id`),
  KEY `order_year` (`order_year`,`order_month`,`order_day`),
  KEY `buyer_user_id` (`buyer_user_id`,`order_state_id`,`kind_id`),
  KEY `store_id` (`store_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='订单信息表';

-- ----------------------------
--  Table structure for `shop_order_item`
-- ----------------------------
