  `comment_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '评价时间',
  `comment_is_anonymous` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '匿名评价',
  `comment_enable` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '评价信息的状态(BOOL): 1-正常显示; 0-禁止显示',
  `comment_store_desc_credit` tinyint(1) unsigned NOT NULL DEFAULT '5' COMMENT '描述相符评分 - order_buyer_evaluation_status , 评价状态改变后不需要再次评论，根据订单走',
  `comment_store_service_credit` tinyint(1) unsigned NOT NULL DEFAULT '5' COMMENT '服务态度评分 - order_buyer_evaluation_status',
  `comment_store_delivery_credit` tinyint(1) unsigned NOT NULL DEFAULT '5' COMMENT '发货速度评分 - order_buyer_evaluation_status',
  PRIMARY KEY (`comment_id`),
  UNIQUE KEY `order_id` (`order_id`,`item_id`),
  KEY `goods_evaluation_ibfk_2` (`user_id`),
  KEY `goods_id` (`product_id`),
  KEY `item_id` (`item_id`),
  KEY `product_id` (`store_id`,`product_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='商品评价表';

-- ----------------------------
--  Table structure for `shop_product_comment_helpful`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_comment_helpful`;
CREATE TABLE `shop_product_comment_helpful` (
  `comment_id` bigint(20) unsigned NOT NULL COMMENT '评论id',
  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '点击用户id',
  `comment_helpful_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '评价时间',
  PRIMARY KEY (`comment_id`,`user_id`),
  KEY `goods_evaluation_comment_ibfk_1` (`comment_id`),
  KEY `goods_evaluation_comment_ibfk_2` (`user_id`),
