) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='商品评价有用投票表';

-- ----------------------------
--  Table structure for `shop_product_comment_reply`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_comment_reply`;
CREATE TABLE `shop_product_comment_reply` (
  `comment_reply_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '评论回复id',
  `comment_id` bigint(20) unsigned NOT NULL COMMENT '评论id',
