-- ----------------------------
--  Table structure for `shop_product_data`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_data`;
CREATE TABLE `shop_product_data` (
  `product_id` bigint(20) unsigned NOT NULL COMMENT '产品id',
  `product_region_district_ids` varchar(1024) NOT NULL DEFAULT '' COMMENT '商品所在地(DOT): 商品所属分站根据省市区街道分割',
