-- ----------------------------
--  Table structure for `shop_product_detail`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_detail`;
CREATE TABLE `shop_product_detail` (
  `product_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '产品id-定为SPU编号',
  `product_detail` varchar(15000) NOT NULL DEFAULT '' COMMENT '商品描述',
  `product_param` varchar(3000) NOT NULL DEFAULT '' COMMENT '规格参数',
  `product_service` varchar(3000) NOT NULL DEFAULT '' COMMENT '售后服务',
  `product_meta_title` varchar(50) NOT NULL DEFAULT '' COMMENT 'Meta Tag 标题',
  `product_meta_description` varchar(100) NOT NULL DEFAULT '' COMMENT 'Meta Tag 描述',
