-- ----------------------------
--  Table structure for `shop_product_image`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_image`;
CREATE TABLE `shop_product_image` (
  `product_image_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '商品图片Id',
  `product_id` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '产品id:product_id-color_id',
  `store_id` int(10) unsigned NOT NULL COMMENT '店铺id',
  `color_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '系统默认颜色规格Id/spec_item_id, 如果没有则一条记录为0',
  `color_name` varchar(10) NOT NULL DEFAULT '' COMMENT '规格值',
  `item_image_default` varchar(255) NOT NULL DEFAULT '' COMMENT '商品主图',
  `item_image_other` varchar(800) NOT NULL DEFAULT '' COMMENT '副图(DOT)',
  PRIMARY KEY (`product_image_id`),
  UNIQUE KEY `p-color_id` (`product_id`,`color_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='产品图片表，按照颜色规格';

-- ----------------------------
--  Table structure for `shop_product_index`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_index`;
CREATE TABLE `shop_product_index` (
  `product_id` bigint(20) unsigned NOT NULL COMMENT '产品id:定为SPU编号',
  `product_name` varchar(50) NOT NULL DEFAULT '' COMMENT '产品名称:店铺平台先在对用表中检索后通过id检索,检索使用',
  `product_name_index` varchar(255) NOT NULL DEFAULT '' COMMENT '名称索引关键字(DOT)',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店铺id',
  `store_is_open` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '店铺状态:0-关闭;  1-运营中商品是否可用，需要判断该商品店铺状态 冗余列表检索使用',
  `store_category_ids` varchar(50) NOT NULL DEFAULT '0' COMMENT '店铺分类id(DOT)',
  `category_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '商品分类',
  `category_ids` varchar(50) NOT NULL DEFAULT '' COMMENT '商品分类Id(DOT)',
  `type_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '类型id:冗余检索',
  `product_quantity` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品库存:冗余计算',
  `product_warn_quantity` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '预警数量',
  `brand_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '品牌id',
  `product_service_type_ids` varchar(50) NOT NULL DEFAULT '' COMMENT '售后服务(DOT)',
  `product_state_id` smallint(4) unsigned NOT NULL DEFAULT '1' COMMENT '商品状态:1001-正常;1002-下架仓库中;1003-待审核; 1000-违规禁售',
  `product_verify_id` smallint(4) unsigned NOT NULL DEFAULT '10' COMMENT '商品审核(ENUM):3001-审核通过;3002-审核中;3000-审核未通过',
  `product_is_invoices` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否开具增值税发票: 1-是; 0-否',
  `product_is_return` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否允许退换货: 1-是; 0-否',
  `product_is_recommend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '商品推荐:1-是; 0-否',
  `product_stock_status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '缺货状态:1-有现货;2-预售商品;3-缺货;4-2至3天',
  `kind_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '商品种类:1201-实物;1202-虚拟',
  `activity_type_ids` varchar(50) NOT NULL DEFAULT '' COMMENT '参与活动(DOT)',
  `contract_type_ids` varchar(50) NOT NULL DEFAULT '' COMMENT '消费者保障(DOT):由店铺映射到商品',
  `product_assist_data` varchar(255) NOT NULL DEFAULT '' COMMENT '辅助属性值列(DOT):assist_item_id每个都不用 , setFilter(tagid, array(2,3,4));是表示含有标签值2,3,4中的任意一个即符合筛选，这里是or关系。 setFilter(‘tagid’, array(2)); setFilter(‘tagid’, array(3)); 形成and关系| msyql where FIND_IN_SET(''1'', product_assist_data) ',
  `product_add_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `product_unit_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品单价',
  `product_unit_points` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品积分',
  `product_sale_num` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '销售量',
  `product_favorite_num` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '收藏数量人气',
  `product_sale_district_ids` varchar(1024) NOT NULL DEFAULT '' COMMENT '销售区域(DOT): district_id0为不限制查询带入条件',
  `product_region_district_ids` varchar(1024) NOT NULL DEFAULT '' COMMENT '所属区域(DOT): 商品所属分站根据省市区街道分割',
  `product_freight` decimal(6,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '运费:包邮为0，检索使用',
  `product_evaluation_num` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '评价次数',
  `product_tags` varchar(255) NOT NULL DEFAULT '' COMMENT '商品标签(DOT)',
  `store_is_selfsupport` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否自营(BOOL):1-自营;0-非自营',
  PRIMARY KEY (`product_id`),
  KEY `category_id` (`category_id`),
  KEY `type_id` (`type_id`),
  KEY `product_state_id` (`product_state_id`),
  KEY `product_verify_id` (`product_verify_id`),
  KEY `product_assist_data` (`product_assist_data`),
  KEY `product_name_index` (`product_name_index`),
  KEY `store_category_ids` (`store_category_ids`),
  KEY `category_ids` (`category_ids`),
  KEY `activity_type_ids` (`activity_type_ids`),
  KEY `product_name_index_2` (`product_name_index`),
  KEY `product_name` (`product_name`),
  CONSTRAINT `shop_product_index_ibfk_1` FOREIGN KEY (`category_id`) REFERENCES `shop_base_product_category` (`category_id`) ON UPDATE CASCADE,
  CONSTRAINT `shop_product_index_ibfk_2` FOREIGN KEY (`type_id`) REFERENCES `shop_base_product_type` (`type_id`) ON UPDATE CASCADE,
  CONSTRAINT `shop_product_index_ibfk_3` FOREIGN KEY (`product_state_id`) REFERENCES `shop_base_product_state` (`product_state_id`) ON UPDATE CASCADE,
  CONSTRAINT `shop_product_index_ibfk_4` FOREIGN KEY (`product_verify_id`) REFERENCES `shop_base_product_verify` (`product_verify_id`) ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='产品索引表-不读取数据只读主键';

-- ----------------------------
--  Table structure for `shop_product_info`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_info`;
CREATE TABLE `shop_product_info` (
  `product_id` bigint(20) unsigned NOT NULL COMMENT '产品id',
  `product_number` varchar(50) NOT NULL DEFAULT '' COMMENT 'SPU商家编码:货号',
  `product_barcode` varchar(30) NOT NULL DEFAULT '' COMMENT '商品条形码',
  `product_model` varchar(255) NOT NULL DEFAULT '' COMMENT '商品型号',
  `product_assist` varchar(2048) NOT NULL DEFAULT '' COMMENT '属性(JSON) - 辅助属性及VAL',
  `product_spec` varchar(2048) NOT NULL DEFAULT '' COMMENT '规格(JSON)-规格、规格值、goods_id  规格不需要全选就可以添加对应数据[{''id'' : spec_id, ''name'' : spec_name, ''item'':[{''id'' : spec_item_id, ''name'' : spec_item_name}, {''id'' : spec_item_id, ''name'' : spec_item_name}]},{''id'' : spec_id, ''name'' : spec_name, ''item'':[{''id'' : spec_item_id, ''name'' : spec_item_name}, {''id'' : spec_item_id, ''name'' : spec_item_name}]}]\n',
  `product_uniqid` varchar(4096) NOT NULL DEFAULT '[]' COMMENT '商品SKU(JSON):{''uniq_id'':item_id, ''uniq_id'':item_id}',
  `unit_length_id` mediumint(8) unsigned NOT NULL DEFAULT '1' COMMENT '长度单位',
  `unit_weight_id` mediumint(8) unsigned NOT NULL DEFAULT '1' COMMENT '重量单位',
  `product_weight` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品重量',
  `product_cubage` varchar(50) NOT NULL DEFAULT '0.00' COMMENT '尺寸长 x 宽 x 高(DOT):商品体积',
  `product_subtract` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '扣减库存',
  `unit_type_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '计量单位',
  `tax_class_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品税别:商品税率可以按照商品分类来设置',
  `product_buy_limit` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '每人限购',
  `product_user_level_discount` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '参加会员等级折扣',
  `product_shop_card_discount` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '参加店铺发放的会员卡折扣',
  PRIMARY KEY (`product_id`),
  CONSTRAINT `shop_product_info_ibfk_1` FOREIGN KEY (`product_id`) REFERENCES `shop_product_base` (`product_id`) ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='商品信息表';

-- ----------------------------
--  Table structure for `shop_product_item`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_item`;
CREATE TABLE `shop_product_item` (
  `item_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '商品id-SKU编号',
  `item_name` varchar(255) NOT NULL DEFAULT '' COMMENT '副标题(DOT):SKU名称',
  `product_id` bigint(20) unsigned NOT NULL COMMENT '产品id',
  `color_id` mediumint(8) NOT NULL DEFAULT '0' COMMENT '颜色SKU，规格值',
  `item_is_default` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否为默认展示的商品-列表页展示，必须为item_enable',
  `item_number` varchar(50) NOT NULL DEFAULT '' COMMENT 'SKU商家编码:SKU商家编码为非必填项，若不填写，系统会自动生成一个SKU商家编码。',
  `item_barcode` varchar(20) NOT NULL DEFAULT '' COMMENT '条形码',
  `item_cost_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '成本价',
  `item_unit_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品价格',
  `item_market_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '市场价',
  `item_unit_points` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '积分价格',
  `item_quantity` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品库存',
  `item_quantity_frozen` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品冻结库存',
  `item_warn_quantity` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '库存预警值',
  `item_spec` varchar(255) NOT NULL DEFAULT '' COMMENT '商品规格序列化(JSON):{spec_id:spec_item_id, spec_id:spec_item_id, spec_id:spec_item_id}',
  `item_enable` smallint(4) unsigned NOT NULL DEFAULT '1002' COMMENT '是否启用(LIST):1001-正常;1002-下架仓库中;1000-违规禁售',
  `item_is_change` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '被改动(BOOL):0-未改动;1-已改动分销使用',
  `item_weight` decimal(20,6) unsigned NOT NULL DEFAULT '0.000000' COMMENT '商品重量',
  PRIMARY KEY (`item_id`),
  KEY `item_base_ibfk_1` (`product_id`,`color_id`,`item_enable`,`item_is_default`),
  CONSTRAINT `shop_product_item_ibfk_1` FOREIGN KEY (`product_id`) REFERENCES `shop_product_base` (`product_id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='商品表-SKU表   商品名称（产品名称+颜色规格名称） =shop_product_item';

-- ----------------------------
--  Table structure for `shop_product_item_seq`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_item_seq`;
CREATE TABLE `shop_product_item_seq` (
  `product_item_seq_id` varchar(32) NOT NULL DEFAULT '' COMMENT '编号:md5product_item_seq_val',
  `product_item_seq_val` varchar(255) NOT NULL DEFAULT '' COMMENT '编号值',
  `item_id` bigint(20) unsigned NOT NULL COMMENT '商品id-SKU编号',
  `product_id` bigint(20) unsigned NOT NULL COMMENT '产品id',
  KEY `item_base_ibfk_1` (`product_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='ITEM_ID/SKU唯一编号表。';

-- ----------------------------
--  Table structure for `shop_product_meta`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_meta`;
CREATE TABLE `shop_product_meta` (
  `meta_id` varchar(50) NOT NULL COMMENT 'Meta id:data_id-meta_key',
  `data_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'product_id',
  `meta_key` varchar(255) NOT NULL DEFAULT '' COMMENT '键',
  `meta_value` varchar(1024) NOT NULL DEFAULT '' COMMENT '值',
  `meta_datatype` enum('string','json','number') NOT NULL DEFAULT 'string' COMMENT '数据类型',
  PRIMARY KEY (`meta_id`),
  KEY `user_id` (`data_id`),
  KEY `meta_key` (`meta_key`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='产品数据扩展表-要求买家留言可以在此处';

-- ----------------------------
--  Table structure for `shop_product_option`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_option`;
CREATE TABLE `shop_product_option` (
  `product_id` bigint(20) unsigned NOT NULL COMMENT '产品id',
  `product_service_text` varchar(50) NOT NULL DEFAULT '' COMMENT '售后服务-售后服务如不填写，将调用 "商家管理中心 -> 商品 -> 售后服务" 中自定义的\n售后服务-售后服务如不填写，将调用 "商家管理中心 -> 商品 -> 售后服务" 中自定义的\n售后服务-售后服务如不填写，将调用 "商家管理中心 -> 商品 -> 售后服务" 中自定义的',
  PRIMARY KEY (`product_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='商品扩展字段表';

-- ----------------------------
--  Table structure for `shop_product_option_value`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_option_value`;
CREATE TABLE `shop_product_option_value` (
  `product_option_value_id` int(11) NOT NULL AUTO_INCREMENT,
  `product_option_id` int(11) NOT NULL,
  `product_id` int(11) NOT NULL,
  `option_id` int(11) NOT NULL,
  `option_value_id` int(11) NOT NULL,
  `quantity` int(3) NOT NULL,
  `subtract` tinyint(1) NOT NULL,
  `price` decimal(15,4) NOT NULL,
  `price_prefix` varchar(1) NOT NULL,
  `points` int(8) NOT NULL,
  `points_prefix` varchar(1) NOT NULL,
  `weight` decimal(15,8) NOT NULL,
  `weight_prefix` varchar(1) NOT NULL,
  PRIMARY KEY (`product_option_value_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

-- ----------------------------
--  Table structure for `shop_product_pre_sale`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_pre_sale`;
CREATE TABLE `shop_product_pre_sale` (
  `product_id` bigint(20) unsigned NOT NULL COMMENT '产品id',
  `product_pre_sale_end` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '预售结束时间-活动商品信息中存储',
  `product_pss_start` date NOT NULL DEFAULT '0000-00-00' COMMENT '预计发货时间',
  `product_pss_end` date NOT NULL DEFAULT '0000-00-00' COMMENT '预计发货时间',
  PRIMARY KEY (`product_id`),
  CONSTRAINT `shop_product_pre_sale_ibfk_1` FOREIGN KEY (`product_id`) REFERENCES `shop_product_base` (`product_id`) ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='预售商品信息表';

-- ----------------------------
--  Table structure for `shop_product_pricing_policy`
-- ----------------------------
DROP TABLE IF EXISTS `shop_product_pricing_policy`;
CREATE TABLE `shop_product_pricing_policy` (
  `policy_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `item_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所属店铺',
  `customer_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '客户',
  `policy_price` decimal(20,6) NOT NULL DEFAULT '0.000000' COMMENT '策略价格',
