  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户编号',
  `activity_name` varchar(50) NOT NULL DEFAULT '' COMMENT '活动名称',
  `activity_title` varchar(50) NOT NULL DEFAULT '' COMMENT '活动标题',
  `activity_remark` varchar(2014) NOT NULL DEFAULT '' COMMENT '活动说明',
  `activity_combo_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '套餐编号',
  `activity_type_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '活动类型',
  `activity_starttime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '活动开始时间',
  `activity_endtime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '活动结束时间',
