-- ----------------------------
--  Table structure for `shop_store_base`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_base`;
CREATE TABLE `shop_store_base` (
  `store_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '店铺id',
  `user_id` int(10) unsigned NOT NULL COMMENT '用户id:管理员店铺主账号',
  `store_name` varchar(50) NOT NULL DEFAULT '' COMMENT '店铺名称',
  `store_grade_id` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '店铺等级',
  `store_logo` varchar(255) NOT NULL DEFAULT '' COMMENT '店铺logo',
  `store_domain` varchar(255) NOT NULL DEFAULT '' COMMENT '二级域名',
  `store_area` varchar(50) NOT NULL DEFAULT '' COMMENT '所在区域',
  `store_address` varchar(50) NOT NULL DEFAULT '' COMMENT '店铺详细地址',
  `store_latitude` varchar(20) NOT NULL DEFAULT '' COMMENT '纬度',
  `store_longitude` varchar(20) NOT NULL DEFAULT '' COMMENT '经度',
  `store_is_selfsupport` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否自营(ENUM): 1-自营;0-非自营',
  `shop_type` tinyint(4) unsigned NOT NULL DEFAULT '1' COMMENT '店铺类型(ENUM): 1-卖家店铺; 2-供应商店铺',
  `store_is_open` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '店铺状态(ENUM):0-关闭;  1-运营中, 商品是否可用，需要判断该商品店铺状态',
  `shop_parent_id` int(11) NOT NULL DEFAULT '0' COMMENT '上级店铺id:创建店铺决定，所属分销商-不可更改！ 佣金公平性考虑',
  `store_category_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店铺分类id',
  `store_state_id` smallint(4) unsigned NOT NULL DEFAULT '3220' COMMENT '店铺资料信息状态(ENUM):3210-待完善资料;   3220-等待审核 ;  3230-资料审核没有通过;     3240-资料审核通过,待付款',
  `store_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '加入时间',
  `store_end_time` int(11) NOT NULL COMMENT '有效期截止时间',
  `product_category_ids` varchar(255) NOT NULL DEFAULT '' COMMENT '店铺可经营的分类(DOT)',
  `store_o2o_tags` varchar(30) NOT NULL DEFAULT '' COMMENT '免费服务(DOT)',
  `store_o2o_flag` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否O2O(BOOL):0-否;1-是',
  PRIMARY KEY (`store_id`),
  KEY `store_state_id` (`store_is_open`,`store_end_time`),
  KEY `user_id` (`user_id`),
  KEY `store_name` (`store_name`),
  KEY `store_category_id` (`store_category_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺基础信息表';

