  `store_id` int(10) unsigned NOT NULL COMMENT '店铺ID',
  `sc_allow_seller_buy` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '销售员购买权限(BOOL):0-关闭;1-开启,购买权限开启状态下，销售员自己购买的订单将会算入业绩',
  `sc_expenditure` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '分销申请消费额',
  `sc_cps_rate` float(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '一级佣金比例',
  `sc_second_is_enable` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '二级销售(BOOL): 0-关闭; 1-开启',
  `sc_second_cps_rate` float(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '二级佣金比例',
  `sc_third_cps_rate` float(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '三级分佣比例',
  `sc_directseller_customer_exptime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '客户关系 期限， 销售员带来的客户（成为店铺的消费者开始计算时间）超过一定期限后，则不再享受分佣。 消费者在店铺消费第一单时间后，在某个期限内消费才可以产生佣金。 ',
  `sc_directseller_exptime_type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '客户有效期(ENUM):1-永久，建立客户关系,客户以后在店铺的购买都分佣; 2-短期，只根据链接购买获取佣金， 且一定期限后，链接失效。 不需要建立客户关系',
  `sc_directseller_rel_exptime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '客户关系保护期 - 带来的客户关系在一定期限内不给抢走， 其它销售可以通过购买链接生效，但是在保护期内部更改关系',
  `sc_is_verify` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '销售员审核(BOOL):0-不需要审核; 1-需要审核',
  `sc_auto_settle` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '结算方式(BOOL):0-手动结算 ;1-自动结算',
  `sc_settle_time_type` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '结算时间',
  `sc_is_use_min_quantity` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '启用商品起订量',
  `sc_is_use_max_quantity` tinyint(1) NOT NULL DEFAULT '0' COMMENT '启用商品限购量',
  `sc_is_use_product_brand` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '启用商品品牌',
  `sc_is_use_product_weight` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '启用商品重量字段',
  `sc_is_use_product_defined` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '启用商品自定义字段',
  `sc_product_tags` varchar(255) NOT NULL DEFAULT '[]' COMMENT '商品标签管理(JSON)',
  `sc_is_necessary_delivery_date` date NOT NULL DEFAULT '0000-00-00' COMMENT '交货日期必填',
  `sc_is_use_order_min_money` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '启用最低下单金额限制(BOOL):0-关闭;1-启用',
  `sc_order_min_money` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '启用最低下单金额限制',
  `sc_deduct_inventory_mode` tinyint(1) NOT NULL DEFAULT '1' COMMENT '订货仓库限制(ENUM):1-按总仓库订购商品,不限制仓库定向供货指定的客户;2-按客户对应仓库订购商品,不限制仓库定向供货指定的客户',
  `sc_inventory_agent_show_type` tinyint(1) unsigned NOT NULL DEFAULT '2' COMMENT '客户订货系统(ENUM):0-不显示库存,客户订货无库存功能;1-显示存库有无客户而在商品下单时显示商品库存有无，但不显示具体数量，未填库存信息显示无库存信息;2-显示存库数量客户订货系统可在商品、下单时显示商品库存数量，未填写库存信息显示无库存信息',
  `sc_is_enabled_inventory_check` tinyint(1) NOT NULL DEFAULT '1' COMMENT '商品库存扣减预购值锁定(ENUM):1-下单扣减锁定;2-支付扣减锁定,数量小于等于零，禁止订货',
  `sc_fund_account_settings` varchar(255) NOT NULL DEFAULT '[]' COMMENT '客户资金账户(JSON):[{"baseId":1,"name":"预付款账户","code":"0001","can_pay":"1","canRecharge":"1","enableStatus":"1","autoAuditFlag":"1"},{"baseId":2,"name":"返点账户","code":"0002","can_pay":"1","canRecharge":"0","enableStatus":"1","autoAuditFlag":"1"},{"baseId":3,"name":"保证金账户","code":"0003","can_pay":"0","canRecharge":"1","enableStatus":"1","autoAuditFlag":"0"}]',
  `sc_order_process` varchar(255) NOT NULL DEFAULT '' COMMENT '订货流程设置(DOT)',
  `sc_order_return_process` varchar(255) NOT NULL DEFAULT '' COMMENT '退货流程设置(DOT)',
  `sc_is_enabled_share` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '启用客户商品分享(BOOL):1-启用;0-禁用',
  `sc_is_enabled_invoicing` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '启用进销存管理(BOOL):1-启用;0-禁用',
  `sc_settle_circle` tinyint(3) unsigned NOT NULL DEFAULT '30' COMMENT '结算周期:30为整月',
  `sc_settle_last_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '上次结算时间',
  `sc_settle_next_time` int(11) NOT NULL DEFAULT '0' COMMENT '下次结算时间',
  `sc_points_consume_rate` decimal(4,2) NOT NULL DEFAULT '0.00' COMMENT '消费送积分比率',
  PRIMARY KEY (`store_id`),
  UNIQUE KEY `config_type` (`store_id`),
  KEY `sc_settle_next_time` (`sc_settle_next_time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺参数设置表';

-- ----------------------------
--  Table structure for `shop_store_contract`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_contract`;
CREATE TABLE `shop_store_contract` (
  `contract_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '关联ID',
  `contract_type_id` int(10) unsigned NOT NULL COMMENT '服务ID',
  `contract_type_name` varchar(50) NOT NULL DEFAULT '' COMMENT '服务类别名称',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店铺编号',
  `store_name` varchar(50) NOT NULL DEFAULT '' COMMENT '商铺名称',
