-- ----------------------------
--  Table structure for `shop_store_contract_log`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_contract_log`;
CREATE TABLE `shop_store_contract_log` (
  `contract_log_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '日志id',
  `contract_id` int(10) NOT NULL COMMENT '服务id',
  `contract_type_id` int(10) NOT NULL COMMENT '服务id',
  `contract_type_name` varchar(50) NOT NULL COMMENT '服务名称',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店铺id',
  `store_name` varchar(50) NOT NULL DEFAULT '' COMMENT '店铺名称',
  `contract_log_operator` varchar(50) NOT NULL COMMENT '操作人',
  `contract_log_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '日志生成时间',
  `contract_log_desc` varchar(255) NOT NULL DEFAULT '' COMMENT '日志内容',
  `contract_deposit` decimal(10,2) NOT NULL COMMENT '支付保证金金额,有正负',
  `contract_log_type` tinyint(1) unsigned NOT NULL DEFAULT '4' COMMENT '类型(ENUM):1-保证金操作; 2-加入操作; 3-退出操作; 4-其它操作',
  `contract_log_state` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态(ENUM):1-待审核(加入/退出); 2-保证金待审核(加入); 3-审核通过(加入/退出); 4-审核不通过(加入/退出); 5-已缴纳保证金',
  `contract_cash_pic` varchar(255) NOT NULL DEFAULT '' COMMENT '保证金图片',
  PRIMARY KEY (`contract_log_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='消费者保障服务保证金缴纳日志表';

-- ----------------------------
--  Table structure for `shop_store_custom_service`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_custom_service`;
CREATE TABLE `shop_store_custom_service` (
