  `custom_service_number` varchar(30) NOT NULL DEFAULT '' COMMENT '客服账号',
  `custom_service_type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '客服类型 0-售前客服 1-售后客服',
  PRIMARY KEY (`custom_service_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺客服表';

-- ----------------------------
--  Table structure for `shop_store_domain`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_domain`;
CREATE TABLE `shop_store_domain` (
  `store_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
