  `store_slide` varchar(1024) NOT NULL DEFAULT '' COMMENT '店铺幻灯片及幻灯片链接(JSON)',
  `store_address` varchar(255) NOT NULL DEFAULT '' COMMENT '详细地址',
  `store_workingtime` varchar(1024) NOT NULL DEFAULT '' COMMENT '工作时间',
  `store_tel` varchar(12) NOT NULL DEFAULT '' COMMENT '卖家电话',
  `store_qq` varchar(20) NOT NULL DEFAULT '' COMMENT 'qq',
