  `store_free_shipping` int(10) NOT NULL DEFAULT '0' COMMENT '免运费额度',
  `store_template` varchar(20) NOT NULL DEFAULT 'shop1' COMMENT '店铺绑定模板',
  `store_print_desc` varchar(255) NOT NULL DEFAULT '' COMMENT '打印订单页面下方说明文字',
  `store_state_id` smallint(4) unsigned NOT NULL DEFAULT '3210' COMMENT '店铺资料信息状态(ENUM):3210-待完善资料;   3220-等待审核 ;  3230-资料审核没有通过;     3240-资料审核通过,待付款',
  `store_payment_state` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '付款状态(ENUM):0-未付款;   1-已付款待审核;   2-审核通过  ',
  `store_start_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '本次开始时间',
  `store_end_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '有效期截止时间',
  `store_close_reason` varchar(255) NOT NULL DEFAULT '' COMMENT '关闭原因',
  `store_points` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '积分',
  `store_notice` varchar(255) NOT NULL DEFAULT '' COMMENT '购买须知',
  `store_opening_hours` varchar(255) NOT NULL DEFAULT '' COMMENT '营业时间',
  `store_close_hours` varchar(255) NOT NULL DEFAULT '' COMMENT '打烊时间',
  `store_points_consume_rate` decimal(4,2) NOT NULL DEFAULT '0.00' COMMENT '消费送积分比率',
  `store_circle` varchar(10) NOT NULL DEFAULT '' COMMENT '所属商圈',
  PRIMARY KEY (`store_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺表-用户可以多店铺-需要分表';

-- ----------------------------
--  Table structure for `shop_store_media`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_media`;
CREATE TABLE `shop_store_media` (
  `media_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '商品图片Id',
  `media_number` char(32) NOT NULL DEFAULT '' COMMENT '附件md5',
  `gallery_id` bigint(20) NOT NULL,
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店铺id',
  `media_url` varchar(255) NOT NULL COMMENT '附件的url默认为原图',
  `media_source` varchar(255) NOT NULL DEFAULT '' COMMENT '源头-网站抓取',
  `media_order` tinyint(4) unsigned NOT NULL DEFAULT '50' COMMENT '排序-越小越靠前',
  `media_domain` varchar(255) NOT NULL DEFAULT '' COMMENT '域名',
  `media_path` varchar(255) NOT NULL DEFAULT '' COMMENT '附件path-本地存储',
  `media_type` enum('video','other','image') NOT NULL DEFAULT 'image' COMMENT 'image|video|',
  `media_image_h` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '高度',
  `media_image_w` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '宽度',
  `media_size` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '原文件大小',
  `media_mime_type` varchar(100) NOT NULL DEFAULT '' COMMENT '上传的附件类型',
  `media_metadata` varchar(255) NOT NULL DEFAULT '' COMMENT 'metadata',
  `media_alt` varchar(255) NOT NULL DEFAULT '' COMMENT '附件alt',
  `media_name` varchar(50) NOT NULL COMMENT '附件标题',
  `media_desc` varchar(255) NOT NULL COMMENT '附件描述',
  `media_password` varchar(20) NOT NULL DEFAULT '' COMMENT '附件浏览密码',
  `media_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '附件日期',
  PRIMARY KEY (`media_id`),
  KEY `gallery_id` (`gallery_id`,`media_type`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='用户附件表-图片、视频';

-- ----------------------------
--  Table structure for `shop_store_media_gallery`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_media_gallery`;
CREATE TABLE `shop_store_media_gallery` (
  `gallery_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '商品图片Id',
  `gallery_name` varchar(100) NOT NULL DEFAULT '' COMMENT '相册名称',
  `gallery_desc` varchar(255) NOT NULL DEFAULT '' COMMENT '相册描述',
  `gallery_num` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '内容数量',
  `gallery_is_default` tinyint(4) unsigned NOT NULL DEFAULT '0' COMMENT '默认相册:1-是;0-否',
  `gallery_order` smallint(4) unsigned NOT NULL DEFAULT '50' COMMENT '排序',
  `gallery_type` enum('video','other','image') NOT NULL DEFAULT 'image' COMMENT '附件册',
  `gallery_cover` varchar(255) NOT NULL DEFAULT '' COMMENT '封面',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所属店铺',
  PRIMARY KEY (`gallery_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='用户相册表';

-- ----------------------------
--  Table structure for `shop_store_message_setting`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_message_setting`;
CREATE TABLE `shop_store_message_setting` (
  `sms_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `store_id` int(10) unsigned NOT NULL COMMENT '用户id',
  `message_template_ids` varchar(1024) NOT NULL DEFAULT '' COMMENT '开启模板(DOT)',
  `sms_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '设置时间',
  PRIMARY KEY (`sms_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺消息设置表';

-- ----------------------------
--  Table structure for `shop_store_meta`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_meta`;
CREATE TABLE `shop_store_meta` (
  `meta_id` varchar(50) NOT NULL COMMENT 'Meta id = user_id-meta_key',
  `data_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'data_id = store_id',
  `meta_key` varchar(255) NOT NULL COMMENT '键',
  `meta_value` varchar(1024) NOT NULL COMMENT '值',
  `meta_datatype` enum('string','json','number') NOT NULL DEFAULT 'string' COMMENT '数据类型',
  PRIMARY KEY (`meta_id`),
  KEY `user_id` (`data_id`),
  KEY `meta_key` (`meta_key`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺扩展表';

-- ----------------------------
--  Table structure for `shop_store_nav`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_nav`;
CREATE TABLE `shop_store_nav` (
  `store_nav_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '索引ID',
  `store_nav_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '类别，0自定义导航，1商品分类，2文章导航，3活动导航，默认为0',
  `store_nav_title` varchar(100) NOT NULL COMMENT '导航标题',
  `store_nav_url` varchar(255) NOT NULL COMMENT '导航链接',
  `store_nav_position` tinyint(1) NOT NULL DEFAULT '0' COMMENT '导航位置，0头部，1中部，2底部，默认为0',
  `store_nav_target_blank` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否以新窗口打开，0为否，1为是，默认为0',
  `store_nav_order` tinyint(3) unsigned NOT NULL DEFAULT '255' COMMENT '排序',
  `store_nav_content` text NOT NULL COMMENT '导航内容',
  `store_nav_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`store_nav_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺页面导航表';

-- ----------------------------
--  Table structure for `shop_store_points_item`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_points_item`;
CREATE TABLE `shop_store_points_item` (
  `points_item_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '积分礼品索引id',
  `points_item_number` varchar(50) NOT NULL DEFAULT '' COMMENT '积分礼品货号',
  `points_item_name` varchar(100) NOT NULL DEFAULT '' COMMENT '积分礼品名称',
  `points_item_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '积分礼品原价',
  `points_item_points` int(10) NOT NULL DEFAULT '0' COMMENT '积分礼品兑换所需积分',
  `points_item_image` varchar(255) NOT NULL DEFAULT '' COMMENT '积分礼品默认封面图片',
  `points_item_tag` varchar(100) NOT NULL DEFAULT '' COMMENT '积分礼品标签(DOT)',
  `points_item_storage` int(10) NOT NULL DEFAULT '0' COMMENT '积分礼品库存数',
  `points_item_enable` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '积分礼品上架(BOOL): 0-表示下架;1-表示上架',
  `points_item_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '积分礼品是否推荐(BOOL):1-是;0-否',
  `points_item_add_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '积分礼品添加时间',
  `points_item_keywords` varchar(100) NOT NULL DEFAULT '' COMMENT '积分礼品关键字',
  `points_item_description` varchar(200) NOT NULL DEFAULT '' COMMENT '积分礼品描述',
  `points_item_content` text NOT NULL COMMENT '积分礼品详细内容',
  `points_item_salenum` int(10) NOT NULL DEFAULT '0' COMMENT '积分礼品售出数量',
  `points_item_view` int(10) NOT NULL DEFAULT '0' COMMENT '积分商品浏览次数',
  `points_item_user_level` int(10) NOT NULL DEFAULT '0' COMMENT '换购针对会员等级限制，默认为0,所有等级都可换购',
  `points_item_islimit` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否限制每会员兑换数量(BOOL):0-不限制;1-限制',
  `points_item_limitnum` int(10) NOT NULL COMMENT '每会员限制兑换数量',
  `points_item_islimittime` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否限制兑换时间(BOOL):0-不限制；1-为限制',
  `points_item_starttime` int(11) NOT NULL DEFAULT '0' COMMENT '兑换开始时间',
  `points_item_endtime` int(11) NOT NULL DEFAULT '0' COMMENT '兑换结束时间',
  `points_item_order` int(10) NOT NULL DEFAULT '50' COMMENT '礼品排序',
  PRIMARY KEY (`points_item_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

-- ----------------------------
--  Table structure for `shop_store_product_category`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_product_category`;
CREATE TABLE `shop_store_product_category` (
  `store_product_cat_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '分类编号',
  `store_product_cat_name` varchar(50) NOT NULL DEFAULT '' COMMENT '分类名称',
  `store_product_cat_image` varchar(255) NOT NULL DEFAULT '' COMMENT '分类图片',
  `store_id` int(10) unsigned NOT NULL COMMENT '所属店铺',
  `parent_id` int(10) NOT NULL DEFAULT '0' COMMENT '父编号',
  `store_product_cat_enable` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否启用(BOOL):0-禁用;1-启用',
  `store_product_cat_order` tinyint(1) unsigned NOT NULL DEFAULT '50' COMMENT '排序',
  `store_product_cat_level` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '分类等级',
  `store_product_cat_is_leaf` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否叶节点',
  PRIMARY KEY (`store_product_cat_id`),
  KEY `store_id` (`store_id`),
  KEY `parent_id` (`parent_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺商品分类表';

-- ----------------------------
--  Table structure for `shop_store_product_format`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_product_format`;
CREATE TABLE `shop_store_product_format` (
  `product_format_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '版式id',
  `product_format_name` varchar(50) NOT NULL COMMENT '版式名称',
  `store_id` int(10) unsigned NOT NULL,
  `product_format_header_content` varchar(5000) NOT NULL DEFAULT '' COMMENT '版式内容(html)',
  `product_format_footer_content` varchar(5000) NOT NULL DEFAULT '' COMMENT '版式内容(html)',
  PRIMARY KEY (`product_format_id`),
  KEY `store_id` (`store_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='关联板式表';

-- ----------------------------
--  Table structure for `shop_store_product_tag`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_product_tag`;
CREATE TABLE `shop_store_product_tag` (
  `store_product_tag_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '兴趣标签id',
  `product_tag_id` int(10) NOT NULL DEFAULT '0' COMMENT '标签id',
  `product_tag_label` varchar(20) NOT NULL DEFAULT '' COMMENT 'product_tag_name',
  `store_id` int(10) NOT NULL DEFAULT '0' COMMENT '店铺id',
  `product_tag_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '选择标签时间',
  PRIMARY KEY (`store_product_tag_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='商家商品标签表';

-- ----------------------------
--  Table structure for `shop_store_renewal_apply`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_renewal_apply`;
CREATE TABLE `shop_store_renewal_apply` (
  `store_renewal_apply_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `user_id` int(10) unsigned NOT NULL COMMENT '会员id',
  `store_id` int(10) unsigned NOT NULL COMMENT '店铺id',
  `store_name` varchar(50) NOT NULL COMMENT '店铺名称',
  `store_grade_id` int(10) unsigned NOT NULL COMMENT '店铺等级id',
  `store_grade_name` varchar(50) NOT NULL COMMENT '店铺等级名称',
  `store_grade_fee` decimal(10,2) NOT NULL COMMENT '店铺等级费用',
  `store_renewal_apply_renewtime` int(10) unsigned NOT NULL COMMENT '续费时长',
  `store_renewal_apply_expense` decimal(10,2) NOT NULL COMMENT '续费总费用',
  `store_renewal_apply_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '申请时间',
  `store_renewal_apply_starttime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '有效期开始时间',
  `store_renewal_apply_endtime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '有效期结束时间',
  `store_renewal_apply_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态',
  `admin_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '管理员id',
  `admin_name` varchar(50) NOT NULL DEFAULT '' COMMENT '管理员名称',
  `store_renewal_apply_desc` varchar(100) NOT NULL COMMENT '备注',
  PRIMARY KEY (`store_renewal_apply_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='续费申请表\r\n';

-- ----------------------------
--  Table structure for `shop_store_setting`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_setting`;
CREATE TABLE `shop_store_setting` (
  `store_setting_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '店铺id',
  `store_id` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '店铺等级',
  `store_is_brand` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '启用商品品牌(BOOL):1-启用;0-禁用启用商品品牌后，商品将增加品牌选项，且商品品牌将作为商品筛选条件。',
  `store_is_weight` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '启用商品重量:0-关闭;  1-启用',
  `shop_parent_id` int(11) NOT NULL DEFAULT '0' COMMENT '上级店铺id:创建店铺决定，所属分销商-不可更改！ 佣金公平性考虑',
  `store_category_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店铺分类id',
  `store_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '加入时间',
  PRIMARY KEY (`store_setting_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺设置表';

-- ----------------------------
--  Table structure for `shop_store_shipping_address`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_shipping_address`;
CREATE TABLE `shop_store_shipping_address` (
  `ss_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '地址Id',
  `ss_name` varchar(10) NOT NULL DEFAULT '' COMMENT '联系人',
  `ss_mobile` bigint(11) NOT NULL COMMENT '手机号码',
  `ss_telephone` varchar(30) NOT NULL DEFAULT '' COMMENT '联系电话',
  `ss_contacter` varchar(20) NOT NULL DEFAULT '' COMMENT '联系人(未启用)',
  `ss_postalcode` varchar(30) NOT NULL DEFAULT '' COMMENT '联系IM:QQ',
  `ss_province_id` mediumint(8) NOT NULL DEFAULT '0' COMMENT '省id',
  `ss_province` varchar(10) NOT NULL COMMENT '省份',
  `ss_city_id` mediumint(8) NOT NULL DEFAULT '0' COMMENT '市id',
  `ss_city` varchar(10) NOT NULL COMMENT '市',
  `ss_county_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '县',
  `ss_county` varchar(10) NOT NULL COMMENT '县区',
  `ss_address` varchar(50) NOT NULL DEFAULT '' COMMENT '详细地址-不必重复填写地区',
  `ss_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  `ss_is_default` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '默认地址(ENUM):0-否;1-是',
  `store_id` int(10) NOT NULL DEFAULT '0' COMMENT '所属店铺',
  PRIMARY KEY (`ss_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='发货地址表';

-- ----------------------------
--  Table structure for `shop_store_themes`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_themes`;
CREATE TABLE `shop_store_themes` (
  `app_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '应用编号',
  `app_name` varchar(1024) NOT NULL DEFAULT '' COMMENT '应用名称',
  `app_code` text NOT NULL COMMENT '应用代码',
  `tpl_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '模板编号',
  `store_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '所属店铺',
  `user_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '所属用户',
  `subsite_id` mediumint(4) unsigned NOT NULL DEFAULT '0' COMMENT '所属分站:0-总站',
  `app_buildin` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否内置(BOOL):0-否;1-是',
  `app_type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '类型(ENUM):1-WAP;2-PC;3-App',
  `app_tpl` text NOT NULL COMMENT '应用模板',
  PRIMARY KEY (`app_id`),
  UNIQUE KEY `tpl_id` (`store_id`,`tpl_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='店铺风格表';

-- ----------------------------
--  Table structure for `shop_store_transport_item`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_transport_item`;
CREATE TABLE `shop_store_transport_item` (
  `transport_item_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `transport_type_id` mediumint(8) unsigned NOT NULL COMMENT '自定义物流模板ID',
  `logistics_type` varchar(50) NOT NULL DEFAULT '' COMMENT 'EMS,平邮,快递-废弃',
  `transport_item_default_num` smallint(3) unsigned NOT NULL DEFAULT '0' COMMENT '默认数量',
  `transport_item_default_price` decimal(6,2) NOT NULL DEFAULT '0.00' COMMENT '默认运费',
  `transport_item_add_num` smallint(3) NOT NULL DEFAULT '1' COMMENT '增加数量',
  `transport_item_add_price` decimal(6,2) NOT NULL DEFAULT '0.00' COMMENT '增加运费',
  `transport_item_city_ids` varchar(5000) NOT NULL DEFAULT '' COMMENT '区域城市id(DOT):分区域全国都可售卖使用 * 代替id 价格需要根据重量等等计算物流费用',
  `transport_city_ids` text NOT NULL COMMENT '区域城市id(JSON)',
  `transport_item_city_name` varchar(5000) NOT NULL DEFAULT '[]' COMMENT '区域城市名称(JSON)',
  PRIMARY KEY (`transport_item_id`),
  KEY `temp_id` (`transport_type_id`,`logistics_type`) COMMENT '(null)',
  KEY `transport_item_city_ids` (`transport_item_city_ids`(255))
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='自定义物流模板内容表-只处理区域及运费。';

-- ----------------------------
--  Table structure for `shop_store_transport_offpay_area`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_transport_offpay_area`;
CREATE TABLE `shop_store_transport_offpay_area` (
  `offpay_area_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店铺id',
  `offpay_area_city_ids` varchar(5000) NOT NULL DEFAULT '' COMMENT '区域城市id(DOT)',
  PRIMARY KEY (`offpay_area_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='到付区域。';

-- ----------------------------
--  Table structure for `shop_store_transport_type`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_transport_type`;
CREATE TABLE `shop_store_transport_type` (
  `transport_type_id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '物流及售卖区域id',
  `transport_type_name` varchar(20) NOT NULL DEFAULT '' COMMENT '模板名称',
  `store_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所属店铺',
  `chain_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '門店ID',
  `transport_type_pricing_method` smallint(1) unsigned NOT NULL DEFAULT '1' COMMENT '计费规则(ENUM):1-按件数;2-按重量;3-按体积',
  `transport_type_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后编辑时间',
  `transport_type_freight_free` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '免运费额度',
  `transport_type_buildin` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '系统内置(BOOL):0-非内置;1-内置',
  `transport_type_free` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '全免运费(BOOL):0-不全免;1-全免（不限制地区且免运费）',
  PRIMARY KEY (`transport_type_id`),
  KEY `user_id` (`store_id`) COMMENT '(null)'
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='自定义物流运费及售卖区域类型表';

-- ----------------------------
--  Table structure for `shop_store_waybill_tpl`
-- ----------------------------
DROP TABLE IF EXISTS `shop_store_waybill_tpl`;
CREATE TABLE `shop_store_waybill_tpl` (
  `waybill_tpl_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `waybill_tpl_name` varchar(20) NOT NULL COMMENT '模板名称',
  `store_id` int(10) unsigned NOT NULL COMMENT '所属店铺',
  `express_id` mediumint(8) NOT NULL COMMENT '物流公司id',
